@admin.route('/edit_profile', methods=['GET', 'POST'])
@login_required
def edit_profile():
    user, status, user_roles = _auth_user(session, 'No Role')
    if status == 401:
        return redirect(url_for('admin.login'))
    elif status == 403:
        return render_template('403.html', user=user, NAV=NAV)
    form = EditProfileForm(request.form)
    if request.method == 'POST':
        # check user permissions
        user.first_name = request.form.get('first_name')
        user.last_name = request.form.get('last_name')
        user.jobtitle = request.form.get('jobtitle')
        user.dept = request.form.get('dept')
        user.about_me = request.form.get('about_me')
        user.loc_city = request.form.get('city')
        user.loc_state = request.form.get('state')
        user.loc_zipcode = request.form.get('zip')
        user.web_tz = request.form.get('web_tz')
        user.email_updates = request.form.get('email_updates') if request.form.get('email_updates') == 'y' else 'n'
        user.app_updates = request.form.get('app_updates') if request.form.get('app_updates') == 'y' else 'n'
        user.text_updates = request.form.get('text_updates') if request.form.get('text_updates') == 'y' else 'n'
        db.session.query(User)\
            .filter(User.id == int(user.id))\
            .update({
                User.first_name: user.first_name,
                User.last_name: user.last_name,
                User.jobtitle: user.jobtitle,
                User.dept: user.dept,
                User.about_me: user.about_me,
                User.loc_city: user.loc_city,
                User.loc_state: user.loc_state,
                User.loc_zipcode: user.loc_zipcode,
                User.web_tz: user.web_tz,
                User.email_updates: user.email_updates,
                User.app_updates: user.app_updates,
                User.text_updates: user.text_updates,
        }, synchronize_session=False)
        db_connection_handler(db)

    NAV['curpage'] = {"name": "Edit Profile"}

    return render_template('admin/edit_profile.html', user_roles=user_roles, NAV=NAV, form=form, username=user.username,
                           emailaddr=user.email,
                           firstname=user.first_name, lastname=user.last_name, jobtitle=user.jobtitle, dept=user.dept,
                           user=user, about_me=user.about_me, city=user.loc_city, state=user.loc_state, zip=user.loc_zipcode,
                           email_updates=user.email_updates, app_updates=user.app_updates, text_updates=user.text_updates,
                           web_tz=user.web_tz, tz_list=tz_list,
                           api_keys=[])


@admin.route('/update_mfa_status', methods=['POST'])
@login_required
def update_mfa_status():
    user, status, user_roles = _auth_user(session, 'No Role')
    if status == 401:
        return redirect(url_for('admin.login'))
    elif status == 403:
        return render_template('403.html', user=user, NAV=NAV)
    # check user permissions

    updated_user = User.query.filter(User.id==user.id).first()
    raw_response = request.form.get('mfa_enabled')
    if raw_response == "1" or raw_response == "0":
        updated_user.mfa_enabled = int(raw_response)
        db.session.add(updated_user)
        db_connection_handler(db)
        return str(200)
    else:
        return str(500)


@admin.route('/display_mfa_qr', methods=['GET'])
@login_required
def display_mfa_qr():
    user, status, user_roles = _auth_user(session, 'No Role')
    if status == 401:
        return redirect(url_for('admin.login'))
    elif status == 403:
        return render_template('403.html', user=user, NAV=NAV)
    NAV['curpage'] = {"name": "Edit Profile"}
    return render_template('admin/display_mfa_qr.html', user_roles=user_roles, NAV=NAV, user=user), 200, {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0'}


